//! Matter TLV encoders and decoders for Soil Measurement Cluster
//! Cluster ID: 0x0430
//!
//! This file is automatically generated from SoilMeasurement.xml

use crate::tlv;
use anyhow;
use serde_json;


// Attribute decoders

/// Decode SoilMoistureMeasurementLimits attribute (0x0000)
pub fn decode_soil_moisture_measurement_limits(inp: &tlv::TlvItemValue) -> anyhow::Result<u8> {
    if let tlv::TlvItemValue::Int(v) = inp {
        Ok(*v as u8)
    } else {
        Err(anyhow::anyhow!("Expected UInt8"))
    }
}

/// Decode SoilMoistureMeasuredValue attribute (0x0001)
pub fn decode_soil_moisture_measured_value(inp: &tlv::TlvItemValue) -> anyhow::Result<Option<u8>> {
    if let tlv::TlvItemValue::Int(v) = inp {
        Ok(Some(*v as u8))
    } else {
        Ok(None)
    }
}


// JSON dispatcher function

/// Decode attribute value and return as JSON string
///
/// # Parameters
/// * `cluster_id` - The cluster identifier
/// * `attribute_id` - The attribute identifier
/// * `tlv_value` - The TLV value to decode
///
/// # Returns
/// JSON string representation of the decoded value or error
pub fn decode_attribute_json(cluster_id: u32, attribute_id: u32, tlv_value: &crate::tlv::TlvItemValue) -> String {
    // Verify this is the correct cluster
    if cluster_id != 0x0430 {
        return format!("{{\"error\": \"Invalid cluster ID. Expected 0x0430, got {}\"}}", cluster_id);
    }

    match attribute_id {
        0x0000 => {
            match decode_soil_moisture_measurement_limits(tlv_value) {
                Ok(value) => serde_json::to_string(&value).unwrap_or_else(|_| "null".to_string()),
                Err(e) => format!("{{\"error\": \"{}\"}}", e),
            }
        }
        0x0001 => {
            match decode_soil_moisture_measured_value(tlv_value) {
                Ok(value) => serde_json::to_string(&value).unwrap_or_else(|_| "null".to_string()),
                Err(e) => format!("{{\"error\": \"{}\"}}", e),
            }
        }
        _ => format!("{{\"error\": \"Unknown attribute ID: {}\"}}", attribute_id),
    }
}

/// Get list of all attributes supported by this cluster
///
/// # Returns
/// Vector of tuples containing (attribute_id, attribute_name)
pub fn get_attribute_list() -> Vec<(u32, &'static str)> {
    vec![
        (0x0000, "SoilMoistureMeasurementLimits"),
        (0x0001, "SoilMoistureMeasuredValue"),
    ]
}

