// Command encoders

/// Encode Open command (0x00)
pub fn encode_open(open_duration: Option<u8>, target_level: u8) -> anyhow::Result<Vec<u8>> {
    let tlv = tlv::TlvItemEnc {
        tag: 0,
        value: tlv::TlvItemValueEnc::StructInvisible(vec![
        (0, tlv::TlvItemValueEnc::UInt8(open_duration.unwrap_or(0))).into(),
        (1, tlv::TlvItemValueEnc::UInt8(target_level)).into(),
        ]),
    };
    Ok(tlv.encode()?)
}

